mapper.select:
SELECT qnano, memberno, seqno, title, content, choice, grpno, indent, ansnum, rdate
FROM qna
WHERE qnano=#{qnano}

mapper.update:
UPDATE qna
SET title=#{title}, content=#{content}, choice=#{choice}
WHERE qnano = #{qnano}

UPDATE qna
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
